mapper.select:
SELECT *
FROM   auto_message
WHERE company_id = #{companyId}
	  and type = #{type}
ORDER BY type ASC

mapper.update:
UPDATE auto_message 
SET    message = #{message}, 
       update_member_id = #{loginId}
WHERE  id = #{id}

mapper.select:
SELECT *
FROM auto_message 
where  company_id = #{companyId}
       and type = #{type}
order  by rand()
limit  1
