mapper.update:
UPDATE	customer_company 
SET		swear_count = #{swearCount}, 
		insult_count = #{insultCount},
		update_member_id = #{loginId}
WHERE	company_id = #{companyId} 
AND		customer_id = #{id}

UPDATE customer_company 
SET    block_member_id = #{loginId}, 
       block_date = NOW(), 
       block_type = #{blockType}, 
       remark = #{remark}, 
       is_block = 1,
       update_member_id = #{loginId}
WHERE  company_id = #{companyId} 
       AND customer_id = #{id}

UPDATE customer_company 
SET    block_member_id = NULL, 
       block_date = NULL, 
       block_type = NULL, 
       is_block = 0,
      update_member_id = #{loginId},
      remark = #{remark}
WHERE  company_id = #{companyId} 
       AND customer_id = #{id}

UPDATE customer_company 
SET    swear_count = swear_count + 1,
       update_member_id = #{loginId}
WHERE  company_id = #{companyId} 
       AND customer_id = #{id}

UPDATE customer_company 
SET    insult_count = insult_count + 1,
       update_member_id = #{loginId}
WHERE  company_id = #{companyId} 
       AND customer_id = #{id}

UPDATE customer_company 
SET    swear_count = 0,
       update_member_id = #{loginId}
WHERE  company_id = #{companyId} 
       AND customer_id = #{id}

UPDATE customer_company 
SET    insult_count = 0,
       update_member_id = #{loginId}
WHERE  company_id = #{companyId} 
       AND customer_id = #{id}
