mapper.select:
select a.id ,a.create_date ,a.gasapp_member_number ,a.member_id ,a.review_score ,ifnull(b.name, '') as member_name ,c.id as customer_id ,ifnull(c.name, '') as customer_name ,ifnull(d.create_date, '') as start_date ,ifnull(d.end_date, '') as end_date from talk_review a left outer join member b on (b.id = a.member_id) left outer join customer2 c on (c.gasapp_member_number = a.gasapp_member_number) left outer join room_join_history d on (d.id = a.chatid) where 1=1 and a.company_id = #{companyId}
and a.create_date between DATE_FORMAT(#{startDate}, '%Y-%m-%d') and adddate(DATE_FORMAT(#{endDate}, '%Y-%m-%d'), 1) -- 기간 조건.
and b.name like concat ('%', #{searchValue}, '%') or c.name like concat ('%', #{searchValue}, '%')
and c.name like concat ('%', #{searchValue}, '%')
and b.name like concat ('%', #{searchValue}, '%')
and a.review_score = #{score}
order by a.review_score ${sort}, a.create_date DESC
Limit #{startNum}, #{pageSize}

select count(*) as cnt from talk_review a left outer join member b on (b.id = a.member_id) left outer join customer2 c on (c.gasapp_member_number = a.gasapp_member_number) left outer join room_join_history d on (d.id = a.chatid) where 1=1 and a.company_id = #{companyId}
and a.create_date between DATE_FORMAT(#{startDate}, '%Y-%m-%d') and adddate(DATE_FORMAT(#{endDate}, '%Y-%m-%d'), 1) -- 기간 조건.
and b.name like concat ('%', #{searchValue}, '%') or c.name like concat ('%', #{searchValue}, '%')
and c.name like concat ('%', #{searchValue}, '%')
and b.name like concat ('%', #{searchValue}, '%')
and a.review_score = #{score}
