mapper.select:
select count(*) as cnt
from Template t
	inner join v_cate c on t.catesm = c.id 
	left outer join TemplateFavorite f on t.id = f.template
where t.cid = #{cid}


 	and t.emp = #{emp}



	and f.emp = #{emp}



	and c.catelg = #{catelg}



	and c.catemd = #{catemd}



	and c.id = #{catesm}





			and t.ask like concat('%', #{keyword}, '%')


			and t.reply like concat('%', #{keyword}, '%')


			and t.id in (
				select template
				from TemplateKeyword
				where keyword in (
					select id
					from Keyword
					where name = #{keyword}
				)
			)


			and t.emp in (
				select id
				from Emp
				where speaker in (
					select id
					from Speaker
					where cid = t.cid
					and name like concat('%', #{keyword}, '%') 
				)
			)

select c.name as sname, c.catelg, c.lname, c.catemd, c.mname, ( select group_concat(k.id) from TemplateKeyword tk inner join Keyword k on tk.keyword = k.id where tk.template = t.id ) as arr_keywords, t.*, ( case when f.emp = #{emp} then 1 else 0 end ) as isfav from Template t inner join v_cate c on t.catesm = c.id left outer join TemplateFavorite f on t.id = f.template where t.cid = #{cid}
and t.emp = #{emp} -- 나의 답변템플릿 조건.
and f.emp = #{emp} -- 즐겨찾기 답변템플릿 조건.
and c.catelg = #{catelg}
and c.catemd = #{catemd}
and c.id = #{catesm}
and t.ask like concat('%', #{keyword}, '%')
and t.reply like concat('%', #{keyword}, '%')
and t.id in ( select template from TemplateKeyword where keyword in ( select id from Keyword where name = #{keyword} ) )
and t.emp in ( select id from Emp where speaker in ( select id from Speaker where cid = t.cid and name like concat('%', #{keyword}, '%') ) )
order by t.id desc limit #{startNum}, #{endNum}

select count(*) as cnt
from Keyword k
where 1=1
and k.cid = #{cid}
and k.name = #{name}

select k.id, k.createdate, k.workdate, k.name, k.cid
from Keyword k
where 1=1
and k.cid = #{cid}
and k.name = #{name}
limit 1
