mapper.select:
	SELECT count(*) as total_count
      FROM 
        template2 t 
        INNER JOIN v_category vc ON t.category_small_id = vc.id 
        LEFT OUTER JOIN template_favorite f ON t.id = f.template_id 
      WHERE t.company_id = #{companyId}

      		AND vc.category_large_id = #{categoryLargeId}


        	AND vc.category_middle_id = #{categoryMiddleId}


        	AND vc.id = #{categorySmallId}


        	AND f.member_id = #{loginId}


        	AND t.member_id = #{loginId}




	        		AND vc.member_name like concat('%', searchValue, '%')


	        		AND t.ask like concat('%', searchValue, '%')


	        		AND t.reply like concat('%', searchValue, '%')


	        		AND t.id in (
	        			select distinct(tk.template_id)
						from template_keyword tk 
							 inner join keyword2 k on (k.id  = tk.keyword_id)
						where 1=1
						and k.name like concat('%', searchValue, '%')
	        		)

SELECT vc.name AS category_small_name, vc.category_large_id, vc.category_large_name, vc.category_middle_id, vc.category_middle_name, ( SELECT GROUP_CONCAT(k.id) FROM template_keyword tk INNER JOIN keyword2 k ON tk.keyword_id = k.id WHERE tk.template_id = t.id ) AS keyword_ids, t.*, ( CASE WHEN f.member_id = #{loginId} THEN 1 ELSE 0 END ) AS is_favortie FROM template2 t INNER JOIN v_category vc ON t.category_small_id = vc.id LEFT OUTER JOIN template_favorite f ON t.id = f.template_id WHERE t.company_id = #{companyId}
AND vc.category_large_id = #{categoryLargeId}
AND vc.category_middle_id = #{categoryMiddleId}
AND vc.id = #{categorySmallId}
AND f.member_id = #{loginId}
AND t.member_id = #{loginId}
AND vc.member_name like concat('%', searchValue, '%')
AND t.ask like concat('%', searchValue, '%')
AND t.reply like concat('%', searchValue, '%')
AND t.id in ( select distinct(tk.template_id) from template_keyword tk inner join keyword2 k on (k.id = tk.keyword_id) where 1=1 and k.name like concat('%', searchValue, '%') )
ORDER BY t.id DESC limit #{startNum}, #{pageSize}

mapper.delete:
DELETE 
FROM template_favorite
WHERE template_id = #{templateId}
AND member_id = #{loginId}

mapper.select:
select count(*) as cnt
from Keyword k
where 1=1
and k.cid = #{cid}
and k.name = #{name}
